  const [correctMsg, setCorrectMsg] = useState(false);
  const [incorrectMsg, setIncorrectMsg] = useState(false);
  // const [answer, setAnswer] = useState("Teeth");
  const answer = "Teeth";
  const [userAnswer, setUserAnswer] = useState("");
  const [disabled, setDisabled] = useState(false);

  const handleUserAnswer = (e) => {
    setUserAnswer(e.target.value);
  }

  const checkAnswer = () => {
    // setUserAnswer(userAnswer.toLowerCase());
    // setAnswer(answer.toLowerCase());

    // console.log("userAnswer = " + userAnswer);
    // console.log("answer = " + answer);

    if(userAnswer.toLowerCase() === answer.toLowerCase()) {
      console.log("correct!")
      setCorrectMsg(true);
      setDisabled(true);
    }
    else {
      console.log("incorrect!")
      setIncorrectMsg(true);
      // console.log("incorrectMsg = "+incorrectMsg+" (should be true)")
    }
  
  }

  const handleCheck = () => {
    // checkAnswer();
    setUserAnswer(userAnswer.toLowerCase());
    setAnswer(answer.toLowerCase());

    console.log("userAnswer = " + userAnswer);
    console.log("answer = " + answer);

    if(userAnswer === answer) {
      console.log("correct!")
      setCorrectMsg(true);
      setDisabled(true);
      
      // setTimeout(() => {
      //   setCorrectMsg(false)
      // }, 3000)
    }
    else {
      console.log("incorrect!")
      setIncorrectMsg(true);
      // console.log("incorrectMsg = "+incorrectMsg+" (should be true)")
      // setIncorrectMsg(false)

      setTimeout(() => {
        setIncorrectMsg(false)
      }, 3000) 
    }

    

    console.log("correctMsg = " + correctMsg + "\nincorrectMsg = " + incorrectMsg)
    // console.log("both set to false");

    // if(correctMsg) {}
    // if(incorrectMsg) {}
  }



  // TEST--------------------------------------------
  // useEffect (() => {

