  //     if(userAnswer === answer) {
  //       console.log("correct!")
  //       setCorrectMsg(true);
  //       setDisabled(!disabled);
  //     }
  //     else {
  //       console.log("incorrect!")
  //       setIncorrectMsg(true);
  //       console.log("incorrectMsg = "+incorrectMsg+" (should be true)")
  //     }
    
  //   }
  
  //   const handleCheck = () => {
  //     checkAnswer();
  
  //     console.log("correctMsg = " + correctMsg + "\nincorrectMsg = " + incorrectMsg)
  //     console.log("both set to false");
  
  //     if(correctMsg) {setCorrectMsg(false)}
  //     if(incorrectMsg) {setIncorrectMsg(false)}
  //   }

  //   handleCheck();

  // }, [])
  

  

  return(
    <div className="question-answer-container">

      <div className="details-container">
        <h1 className="details">General Knowledge</h1>
        <h1 className="details"
          style={{color: "#fecd50"}}
        >
          $200
        </h1>
      </div>

      <h1 className="question">
        Whether they've been removed or haven't grown in, a person who's edentulous lacks these
      </h1>

      <div className="input-container">
        <input id="answer"
          placeholder="Answer"
          disabled={disabled}
          onChange={handleUserAnswer}
        />
        <button className="check-btn"
          disabled={disabled}
          onClick={handleCheck}
        >
          Check
        </button>
      </div>

      <div className="result-container">
        <p className={`transparent ${correctMsg ? "correct-message" : ""}`}>You are correct!</p>
        <p className={`transparent ${incorrectMsg ? "incorrect-message" : ""}`}>Try again.</p>

        {/* <p className={`transparent correct-message`}>You are correct!</p>
        <p className={`transparent incorrect-message`}>Try again.</p> */}
      </div>
    </div>
  );
}

export default Question;
