import { useState } from "react";
import "./Question.css";

const Question = () => {

  const [correctMsg, setCorrectMsg] = useState(false);
  const [incorrectMsg, setIncorrectMsg] = useState(false);
  const [answer, setAnswer] = useState("Teeth");
  const [userAnswer, setUserAnswer] = useState("");

  const handleUserAnswer = (e) => {
    setUserAnswer(e.target.value);
  }

  const checkAnswer = () => {
    setUserAnswer(userAnswer.toLowerCase());
    setAnswer(answer.toLowerCase());

    console.log("userAnswer = " + userAnswer);
    console.log("answer = " + answer);

    if(userAnswer === answer) {
      setCorrectMsg(true);
    }
    else {
      if(correctMsg){}
      else{setIncorrectMsg(true);}
    }
  }

  const handleCheck = () => {
    checkAnswer();

    // if(correctMsg) {setCorrectMsg(false)}
    if(incorrectMsg) {setIncorrectMsg(false)}
  }

  return(
    <div className="question-answer-container">

      <div className="details-container">
        <h1 className="details">General Knowledge</h1>
        <h1 className="details"
          style={{color: "#fecd50"}}
        >
          $200
        </h1>
      </div>

      <h1 className="question">
        Whether they've been removed or haven't grown in, a person who's edentulous lacks these
      </h1>

      <div className="input-container">
        <input id="answer"
          placeholder="Answer"
          onChange={handleUserAnswer}
        />
        <button className="check-btn"
          onClick={handleCheck}
        >
          Check
        </button>
      </div>

      {/* <div className="btn-container">
        <button className="details-btn" 
          style={{borderBottomLeftRadius: "12.5px", marginRight: "-1px"}}
        >
          Show Answer
        </button>
        <button className="details-btn"
          style={{borderBottomRightRadius: "12.5px", marginLeft: "-1px"}}
        >
          Details
        </button>
      </div> */}

      <div className="result-container">
        <p className={`transparent ${correctMsg ? "correct-message" : ""}`}>You are correct!</p>
        <p className={`transparent ${incorrectMsg ? "incorrect-message" : ""}`}>Try again.</p>

        {/* <p className={`transparent correct-message`}>You are correct!</p>
        <p className={`transparent incorrect-message`}>Try again.</p> */}
      </div>
      
    </div>
  );
}

export default Question;
