import { useState } from "react";
import "./Question.css";

const Question = () => {

  const [correctMsg, setCorrectMsg] = useState(false);
  const [incorrectMsg, setIncorrectMsg] = useState(false);
  const answer = "Teeth";
  const [userAnswer, setUserAnswer] = useState("");
  const [disabled, setDisabled] = useState(false);
  const [showAnswer, setShowAnswer] = useState(false);

  const handleUserAnswer = (e) => {
    setUserAnswer(e.target.value);
  }

  const handleCheck = () => {
    
    if(userAnswer.toLowerCase() === answer.toLowerCase()) {
      setCorrectMsg(true);
      setDisabled(true);
    }
    else {
      setIncorrectMsg(true);
      
      setTimeout(() => {
        setIncorrectMsg(false);
      }, 2000) 
    }
  }

  const handleShowAnswer = () => {
    setShowAnswer(!showAnswer);
  }

  return(
    <div className="question-answer-container">

      <div className="details-container">
        <h1 className="details">General Knowledge</h1>
        <h1 className="details"
          style={{color: "#fecd50"}}
        >
          $200
        </h1>
      </div>

      <h1 className="question">
        Whether they've been removed or haven't grown in, a person who's edentulous lacks these
      </h1>

      <div className="input-container">
        <input id="answer"
          placeholder="Answer"
          disabled={disabled}
          onChange={handleUserAnswer}
        />
        <button className="check-btn"
          disabled={disabled}
          onClick={handleCheck}
        >
          Check
        </button>
      </div>

      <div className="result-container">
        <p className={`transparent ${correctMsg ? "correct-message" : ""}`}>You are correct!</p>
        <p className={`transparent ${incorrectMsg ? "incorrect-message" : ""}`}>Try again.</p>
      </div>

      <div className="show-ans-container">
        <p className={`${showAnswer ? "answer" : ""}`}>{answer}</p>
        <button className="show-ans-btn" title="Show Answer" onClick={handleShowAnswer}>?</button>
      </div>
      
    </div>
  );
}

export default Question;
